mapper.select:
select * from org_department where delflag='0'
and id=#{id}
and depno=#{depno}
and depname=#{depname}
limit 1

select * from org_department where delflag='0' 

    order by ${sort} 
    ${order}

limit #{begin}, #{rows}

select * from org_department
where delflag='0' and parentid = #{parentid}
order by sortno

SELECT * FROM org_department WHERE ID IN (
    SELECT id FROM (
        SELECT id, parentid, IF(FIND_IN_SET(parentid, @pids) > 0, @pids := CONCAT(@pids, ',', id), 0) pids
        FROM org_department, (SELECT @pids := #{parentid}) var
        WHERE FIND_IN_SET(parentid, @pids) > 0
    ) tmp
) and delflag='0'

    order by ${sort} 
    ${order}

limit #{begin}, #{rows}

mapper.update:
update org_department
depno=#{depno}, depname=#{depname}, remark=#{remark}, address=#{address}, workphone=#{workphone}, parentid=#{parentid}, deppath=#{deppath}, deplevel=#{deplevel}, managerid=#{managerid}, managername=#{managername}, principalid=#{principalid}, principal=#{principal}, deptype=#{deptype}, virtualflag=#{virtualflag}, sortno=#{sortno}, createtime=#{createtime}, creator=#{creator}, modifytime=#{modifytime}, modifier=#{modifier},
where id = #{id}

update org_department set delflag='1'  
where id in

    #{item}
