mapper.select:
select * from org_employee where delflag='0'
and id=#{id}
and empno=#{empno}
and empname=#{empname}
limit 1

select * from org_employee where delflag='0' 

    order by ${sort} 
    ${order}

limit #{begin}, #{rows}

SELECT * FROM org_employee WHERE departmentid = #{depid}

    order by ${sort} 
    ${order}

limit #{begin}, #{rows}

SELECT * FROM org_employee WHERE departmentid IN (
    select #{depid}
    union
    SELECT id FROM (
        SELECT id, parentid, IF(FIND_IN_SET(parentid, @pids) > 0, @pids := CONCAT(@pids, ',', id), 0) pids
        FROM org_department, (SELECT @pids := #{depid}) var
        WHERE FIND_IN_SET(parentid, @pids) > 0
    ) tmp
) and delflag='0'

    order by ${sort} 
    ${order}

limit #{begin}, #{rows}

SELECT count(1) FROM org_employee WHERE departmentid IN (
    select #{depid}
    union
    SELECT id FROM (
        SELECT id, parentid, IF(FIND_IN_SET(parentid, @pids) > 0, @pids := CONCAT(@pids, ',', id), 0) pids
        FROM org_department, (SELECT @pids := #{depid}) var
        WHERE FIND_IN_SET(parentid, @pids) > 0
    ) tmp
) and delflag='0'

mapper.update:
update org_employee
empno=#{empno}, empname=#{empname}, idnum=#{idnum}, gender=#{gender}, education=#{education}, birthday=#{birthday}, homephone=#{homephone}, telephone=#{telephone}, email=#{email}, entrydate=#{entrydate}, leavedate=#{leavedate}, retiredate=#{retiredate}, photo=#{photo}, departmentid=#{departmentid}, cardno=#{cardno}, empsts=#{empsts}, custid=#{custid}, sortno=#{sortno}, createtime=#{createtime}, modifytime=#{modifytime}, creator=#{creator}, modifier=#{modifier}, delflag=#{delflag},
where id = #{id}

update org_employee set delflag='1'  
where id in

    #{item}
