mapper.select:
select * from org_user where delflag='0'
and id=#{id}
and username=#{username}
limit 1

SELECT u.* FROM org_user u
LEFT JOIN org_employee e ON e.id=u.employeeid and e.delflag='0'
WHERE e.departmentid IN (
    select #{depid}
    union
    SELECT id FROM (
        SELECT id, parentid, IF(FIND_IN_SET(parentid, @pids) > 0, @pids := CONCAT(@pids, ',', id), 0) pids
        FROM org_department, (SELECT @pids := #{depid}) var
        WHERE FIND_IN_SET(parentid, @pids) > 0
    ) tmp
) and u.delflag='0'
limit #{begin}, #{rows}

mapper.update:
update org_user
employeeid=#{employeeid}, username=#{username}, passwd=#{passwd}, email=#{email}, telephone=#{telephone}, esignflag=#{esignflag}, esignature=#{esignature}, epasswd=#{epasswd}, imsicheckflag=#{imsicheckflag}, imsi=#{imsi}, desktopstyle=#{desktopstyle}, enableflag=#{enableflag}, passwdduedate=#{passwdduedate}, errornum=#{errornum}, logintime=#{logintime}, createtime=#{createtime}, modifytime=#{modifytime}, creator=#{creator}, modifier=#{modifier}, delflag=#{delflag},
where id = #{id}

update org_user
username=#{username}, passwd=#{passwd}, email=#{email}, telephone=#{telephone}, esignflag=#{esignflag}, esignature=#{esignature}, epasswd=#{epasswd}, imsicheckflag=#{imsicheckflag}, imsi=#{imsi}, desktopstyle=#{desktopstyle}, enableflag=#{enableflag}, passwdduedate=#{passwdduedate}, errornum=#{errornum}, logintime=#{logintime}, createtime=#{createtime}, modifytime=#{modifytime}, creator=#{creator}, modifier=#{modifier}, delflag=#{delflag},
where employeeid = #{employeeid}

update org_user set delflag='1'  
where employeeid in

    #{item}
