mapper.select:
select b.recordid,m.menuname recordname,a.recount,b.modifytime,b.ipaddress,m.menuurl from (select recordid,sum(recount) recount,max(id) id from portal_recordlog where userid=#{userid} and recordtype='menu' group by recordid ) a left outer join portal_recordlog b on a.id=b.id inner join portal_menu m on m.id=b.recordid
where b.recordname like '%${recordname}%'
order by a.recount desc,b.modifytime desc limit #{begin}, #{rows}
